import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { HashLink as NavLink } from "react-router-hash-link";
import "./Header.scss";
import HeaderLogo from "../../assets/logo_title-nobg.webp";
import "../MenuBurger/MenuBurger.scss";

function Header() {
  const [menuStatus, setMenuStatus] = useState("close");
  const history = useHistory();

  useEffect(() => {
    const unlisten = history.listen(() => {
      setMenuStatus("close");
    });
    return unlisten;
  }, [history]);

  const handleLinkClick = () => {
    setMenuStatus("close");
  };

  return (
    <div className="Header">
      <div className="MenuBurger">
        <div
          className="MenuBurger-container"
          role="button"
          onClick={() =>
            setMenuStatus(menuStatus === "open" ? "close" : "open")
          }
        >
          <i className={menuStatus}></i>
          <i className={menuStatus}></i>
          <i className={menuStatus}></i>
        </div>
      </div>

      <nav className={"NavigationList-wrapper " + menuStatus}>
        <ul className="NavigationList" style={{ paddingLeft: 0 }}>
          <li>
            <Link to="/#" className="underline" onClick={handleLinkClick}>
              Accueil
            </Link>
          </li>
          <li>
            <NavLink
              smooth
              to="/#articles"
              className="underline"
              onClick={handleLinkClick}
            >
              Articles
            </NavLink>
          </li>
          <li>
            <NavLink
              smooth
              to="/#a-propos"
              className="underline"
              onClick={handleLinkClick}
            >
              A propos
            </NavLink>
          </li>
          <li>
            <a
              href="mailto:cybersafe-dls@protonmail.com"
              className="underline"
              onClick={handleLinkClick}
            >
              Contact
            </a>
          </li>
        </ul>
      </nav>
      <span className="LogoSpan">
        <img className="Logo" src={HeaderLogo} />
      </span>
    </div>
  );
}

export default Header;
